mapper.insert:
INSERT 											
INTO USER_STATUS(TBOARD_NO, USER_NO, STATUS)
VALUES ( 	#{tboardNo:NUMERIC},
			#{userNo:NUMERIC},
			#{status:NUMERIC}
			)

mapper.select:
select t.travel,t.title,t.travel,t.travel_start,t.travel_end,t.user_no
	from TBOARD t, USER u
where u.USER_NO=t.USER_NO and u.id=#{id} limit 1;
